import React from 'react';
import ReactDOM from 'react-dom';
import App from './App';
import './styles/global.css';
import { ThemeProvider } from 'styled-components'; // Make sure you’re using styled-components
import { lightTheme, darkTheme } from './theme/theme'; // Option B: importing both themes

const darkMode = false; // or use context/state to toggle themes

ReactDOM.render(
  <React.StrictMode>
    <ThemeProvider theme={darkMode ? darkTheme : lightTheme}>
      <App />
    </ThemeProvider>
  </React.StrictMode>,
  document.getElementById('root')
);
